// App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';
import SearchForm from './components/SearchForm';
import WeatherDisplay from './components/WeatherDisplay';

const App = () => {
  const [weatherData, setWeatherData] = useState(null);
  const [cityName, setCityName] = useState('');

  const handleSearch = async (selectedPrefecture) => {
    if (!selectedPrefecture) {
      // 選択されていない場合は何もしないかエラー処理を行う
      return;
    }

    const apiUrl = `https://weather.tsukumijima.net/api/forecast/prefecture/${selectedPrefecture}`;

    try {
      const response = await axios.get(apiUrl);
      setWeatherData(response.data);
      setCityName(response.data.location.city);
    } catch (e) {
      console.error('Error fetching weather data:', e);
    }
  };

  return (
    <div className="app-container">
      <h1>Weather Forecast App</h1>
      <SearchForm onSearch={handleSearch} />
      {weatherData && <WeatherDisplay cityName={cityName} forecasts={weatherData.forecasts} />}
    </div>
  );
};

export default App;
