import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './App.css';

const App = () => {
  const [cityCode, setCityCode] = useState('');
  const [weatherData, setWeatherData] = useState(null);
  const [cityName, setCityName] = useState('');

  const handleInputChange = (event) => {
    setCityCode(event.target.value);
  };

  const handleSearch = () => {
    const apiUrl = `https://weather.tsukumijima.net/api/forecast/city/${cityCode}`;
    axios.get(apiUrl)
      .then(response => {
        setWeatherData(response.data);
        setCityName(response.data.location.city);
      })
      .catch(error => {
        console.error('Error fetching weather data:', error);
      });
  };

  useEffect(() => {
    console.log('Weather data updated. Current value:', weatherData);
  }, [weatherData]);

  return (
    <div className="app-container">
      <h1>Weather Forecast App</h1>
      <div className="search-container">
        <label htmlFor="cityCodeInput">Enter City Code:</label>
        <input
          type="text"
          id="cityCodeInput"
          value={cityCode}
          onChange={handleInputChange}
        />
        <button onClick={handleSearch}>Search</button>
      </div>
      {weatherData && (
        <div className="weather-container">
          <h2>{`Weather Forecast for ${cityName}`}</h2>
          <ul>
            {weatherData.forecasts.map((forecast, index) => (
              <li key={index}>
                <p>{forecast.dateLabel} {forecast.date}</p>
                <p>{forecast.telop}</p>
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default App;
