// WeatherDisplay.js
import React from "react";

const WeatherDisplay = ({ cityName, forecasts, currentWeather }) => {
  const getWeatherIcon = () => {
    switch (currentWeather) {
      case "晴のち曇":
        return "sunny-icon.png";
      case "雨":
        return "rainy-icon.png";
      case "曇り":
        return "cloudy-icon.png";
      // 他の天気状態にも対応するように追加
      default:
        return "default-icon.png";
    }
  };

  return (
    <div className="weather-container">
      <h2>{`Weather Forecast for ${cityName}`}</h2>
      <img src={getWeatherIcon()} alt="Weather Icon" />
      <ul>
        {forecasts.map((forecast, index) => (
          <li key={index}>
            <p>
              {forecast.dateLabel} {forecast.date}
            </p>
            <p>{`最高気温: ${
              forecast.temperature.max
                ? forecast.temperature.max.celsius
                : "N/A"
            }°C`}</p>
            <p>{`最低気温: ${
              forecast.temperature.min
                ? forecast.temperature.min.celsius
                : "N/A"
            }°C`}</p>
            <p>{forecast.telop}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default WeatherDisplay;
